import React from "react";
import InputSelect from "../components/InputSelect";
import InputField from "../components/InputField";
import { useNavigate } from "react-router-dom"; // Import useNavigate

const InvoiceFilter = (props) => {
    const { handleChange, handleSubmit, filter, buyerList, sellerList, confirm } = props;
    const navigate = useNavigate(); // Initialize useNavigate

    const handleReset = () => {
        // Reset filtry na výchozí hodnoty
        props.handleChange({ target: { name: 'buyerID', value: '' } });
        props.handleChange({ target: { name: 'sellerID', value: '' } });
        props.handleChange({ target: { name: 'minPrice', value: '' } });
        props.handleChange({ target: { name: 'maxPrice', value: '' } });
        props.handleChange({ target: { name: 'limit', value: '' } });

        // Navigace na aktuální URL
        navigate("/invoices");
    };

    return (
        <form onSubmit={handleSubmit}>
            <div className="row">
                <div className="col">
                    <InputSelect
                        name="buyerID"
                        items={buyerList}
                        handleChange={handleChange}
                        label="Odběratel"
                        prompt="nevybrán"
                        value={filter.buyerID}
                    />
                </div>
                <div className="col">
                    <InputSelect
                        name="sellerID"
                        items={sellerList}
                        handleChange={handleChange}
                        label="Dodavatel"
                        prompt="nevybrán"
                        value={filter.sellerID}
                    />
                </div>
            </div>
            <div className="row">
                <div className="col">
                    <InputField
                        type="number"
                        min="0"
                        name="minPrice"
                        handleChange={handleChange}
                        label="Minimální cena"
                        prompt="neuveden"
                        value={filter.minPrice || ''}
                    />
                </div>
                <div className="col">
                    <InputField
                        type="number"
                        min="0"
                        name="maxPrice"
                        handleChange={handleChange}
                        label="Maximální cena"
                        prompt="neuveden"
                        value={filter.maxPrice || ''}
                    />
                </div>
                <div className="col">
                    <InputField
                        type="number"
                        min="1"
                        name="limit"
                        handleChange={handleChange}
                        label="Limit počtu faktur"
                        prompt="neuveden"
                        value={filter.limit || ''}
                    />
                </div>
            </div>
            <div className="row">
                <div className="col">
                    <button
                        type="submit"
                        className="btn btn-secondary float-right mt-2 mr-2"
                    >
                        {confirm}
                    </button>
                    <button
                        type="button"
                        onClick={handleReset}
                        className="btn btn-success float-right mt-2"
                    >
                        Reset
                    </button>
                </div>
            </div>
        </form>
    );
};

export default InvoiceFilter;
