import React, { useState, useEffect } from 'react';
import { apiGet } from '../utils/api';  // Import funkce apiGet

const StatisticsTable = () => {
  const [invoiceStats, setInvoiceStats] = useState(null);
  const [personStats, setPersonStats] = useState(null);

  // Načtení dat o fakturách
  useEffect(() => {
    const fetchInvoiceStats = async () => {
      try {
        const data = await apiGet('/api/invoices/statistics');
        setInvoiceStats(data);
      } catch (error) {
        console.error('Error fetching invoice statistics:', error);
      }
    };
    fetchInvoiceStats();
  }, []);

  // Načtení dat o osobách
  useEffect(() => {
    const fetchPersonStats = async () => {
      try {
        const data = await apiGet('/api/persons/statistics');
        setPersonStats(data);
      } catch (error) {
        console.error('Error fetching person statistics:', error);
      }
    };
    fetchPersonStats();
  }, []);

  return (
    <div>
      {/* Tabulka pro fakturační statistiky */}
      {invoiceStats && (
        <table>
          <thead>
            <tr>
              <th>Current Year Sum</th>
              <th>All Time Sum</th>
              <th>Invoices Count</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{invoiceStats.currentYearSum}</td>
              <td>{invoiceStats.allTimeSum}</td>
              <td>{invoiceStats.invoicesCount}</td>
            </tr>
          </tbody>
        </table>
      )}

      {/* Tabulka pro osobní statistiky */}
      {personStats && (
        <table>
          <thead>
            <tr>
              <th>Person ID</th>
              <th>Person Name</th>
              <th>Revenue</th>
            </tr>
          </thead>
          <tbody>
            {personStats.map(person => (
              <tr key={person.personId}>
                <td>{person.personId}</td>
                <td>{person.personName}</td>
                <td>{person.revenue !== null ? person.revenue : 'N/A'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default StatisticsTable;
